	  public void JZ()                                                                                                                                                                                                                      
	  {                                                                                                                                                                                                                                     
	    this.alu.GetEffectiveAddress(instruction);                                                                                                                                                                                                          
	                                                                                                                                                                                                                                        
	    if (SelectRegister().get().equals(String.format("%020d", new Object[] { Integer.valueOf(0) })))                                                                                                                                     
	    {                                                                                                                                                                                                                                   
	      if (getI().get().equals("0")) {                                                                                                                                                                                                   
	        getPC().set(getMAR().get());                                                                                                                                                                                                    
	      } else {                                                                                                                                                                                                                          
	        getPC().set(getMEMORY().get(getMAR().get()));                                                                                                                                                                                   
	      }                                                                                                                                                                                                                                 
	    } else {                                                                                                                                                                                                                            
	      this.processor.IncreamentProgramCounter();                                                                                                                                                                                                                     
	    }                                                                                                                                                                                                                                   
	  }                                                                                                                                                                                                                                     
	                                                                                                                                                                                                                                        
	                                                                                                                                                                                                                                        
	                                                                                                                                                                                                                                        
	                                                                                                                                                                                                                                        
	                                                                                                                                                                                                                                        
	                                                                                                                                                                                                                                        
	                                                                                                                                                                                                                                        
	                                                                                                                                                                                                                                        
