	  public void EAVector_i(int vector, int i)                                                                                                                                                                                             
	  {                                                                                                                                                                                                                                     
	    if ((getXF().get().equals("00") & getI().get().equals("0")))                                                                                                                                                                        
	    {                                                                                                                                                                                                                                   
	      getMAR().set(getADDR().get() + vector);                                                                                                                                                                                           
	      pause(1);                                                                                                                                                                                                                         
	      getMBR().set(getMEMORY().get(getMAR().get() + i));                                                                                                                                                                                
	      pause(1);                                                                                                                                                                                                                         
	                                                                                                                                                                                                                                        
	                                                                                                                                                                                                                                        
	                                                                                                                                                                                                                                        
	    }                                                                                                                                                                                                                                   
	    else if ((((getXF().get().equals("01")) || (getXF().get().equals("10")) || (getXF().get().equals("11"))) & getI().get().equals("0")))                                                                                               
	    {                                                                                                                                                                                                                                   
	      getALU().OP1.set(getADDR().get() + vector);                                                                                                                                                                                       
	      getALU().OP2.set(SelectIndexRegister().get());                                                                                                                                                                                    
	      getALU().add();                                                                                                                                                                                                                   
	      getMAR().set(getALU().RES.get());                                                                                                                                                                                                 
	      pause(1);                                                                                                                                                                                                                         
	      getMBR().set(getMEMORY().get(getMAR().get() + i));                                                                                                                                                                                
	      pause(1);                                                                                                                                                                                                                         
	                                                                                                                                                                                                                                        
	                                                                                                                                                                                                                                        
	    }                                                                                                                                                                                                                                   
	    else if ((getXF().get().equals("00") & getI().get().equals("1"))) {                                                                                                                                                                 
	      getMAR().set(getADDR().get() + vector);                                                                                                                                                                                           
	      pause(1);                                                                                                                                                                                                                         
	      String memoryAddr = getMAR().get();                                                                                                                                                                                               
	      getMBR().set(getMEMORY().get(memoryAddr));                                                                                                                                                                                        
	      pause(1);                                                                                                                                                                                                                         
	      getMAR().set(getMBR().get());                                                                                                                                                                                                     
	      pause(1);                                                                                                                                                                                                                         
	      getMBR().set(getMEMORY().get(getMAR().get() + i));                                                                                                                                                                                
	      pause(1);                                                                                                                                                                                                                         
	                                                                                                                                                                                                                                        
	    }                                                                                                                                                                                                                                   
	    else                                                                                                                                                                                                                                
	    {                                                                                                                                                                                                                                   
	      getALU().OP1.set(getADDR().get() + vector);                                                                                                                                                                                       
	      getALU().OP2.set(SelectIndexRegister().get());                                                                                                                                                                                    
	      getALU().add();                                                                                                                                                                                                                   
	      getMAR().set(getMEMORY().get(getALU().RES.get()));                                                                                                                                                                                
	      pause(1);                                                                                                                                                                                                                         
	      getMBR().set(getMEMORY().get(getMAR().get() + i));                                                                                                                                                                                
	      pause(1);                                                                                                                                                                                                                         
	    }                                                                                                                                                                                                                                   
	  }                
	  
	  private void AppendInstructionAsText(String sInst)                                                                                                                                                                                    
	  {                                                                                                                                                                                                                                     
	    String sTxt = "";                                                                                                                                                                                                                   
	                                                                                                                                                                                                                                        
	    sTxt = sTxt + sInst + ", ";                                                                                                                                                                                                         
	    sTxt = sTxt + String.format("%s", new Object[] { "R" + Integer.parseInt(getRF().get(), 2) + ", " });                                                                                                                                
	    sTxt = sTxt + (Integer.parseInt(getXF().get(), 2) != 0 ? String.format("%s", new Object[] { Integer.parseInt(getXF().get(), 2) + ", " }) : "");                                                                                     
	    sTxt = sTxt + (Integer.parseInt(getI().get(), 2) != 0 ? String.format("%s", new Object[] { Integer.parseInt(getI().get(), 2) + ", " }) : "");                                                                                       
	    sTxt = sTxt + (Integer.parseInt(getT().get(), 2) != 0 ? String.format("%s", new Object[] { Integer.parseInt(getT().get(), 2) + ", " }) : "");                                                                                       
	    sTxt = sTxt + (Integer.parseInt(getADDR().get(), 2) != 0 ? String.format("%s", new Object[] { Integer.parseInt(getADDR().get(), 2) + ", " }) : "");                                                                                 
	                                                                                                                                           
	  }  
